import React, { useEffect } from 'react';
import ReactDOM from 'react-dom';
import { useSearchParams } from 'react-router-dom';
import axios from 'axios';
import './App_CartItem.css';
import mergeImages from 'merge-images';

import {
    BrowserRouter as Router,
    Switch,
    Route,
    Link,
    useParams
  } from "react-router-dom";

// MUI
import Grid from '@mui/material/Grid';
import Button from '@mui/material/Button';
import { ThemeProvider, createTheme } from '@mui/material/styles';
import IconButton from '@mui/material/IconButton';

// Import constants
import * as GLOBAL_CONSTANTS from '../../GlobalConstants';

function CartItem(props){
    if(props.type == 'sticker'){
        return(  
                <div>
                    <Grid   container 
                            justifyContent="center"
                            alignItems="center">
                        <Grid xs = {1.5} sm = {1}>
                            <h3 className='stickerListIndex'>{props.listIndex}</h3>
                        </Grid>

                            <Grid xs = {3} sm = {2} display="flex" justifyContent="center">
                                <Link to={props.link} target="_blank">
                                    <div className='cartStickerImgWrapper'>
                                        <img className='cartStickerImg' src={props.img}></img>
                                    </div>
                                </Link>
                            </Grid>

                        <Grid xs = {4.5} sm = {6}>
                            <Link to={props.link} target="_blank" style={{ textDecoration: 'none', color: 'inherit' }}>
                                <h3 className='stickerName'>{props.name} ({props.material}, {props.style}) x{props.amount}</h3>
                                <p className='stickerInfo'>{props.price} €/item, {props.width} mm x {props.height} mm</p>
                            </Link>
                        </Grid>
                        <Grid xs = {1} sm = {1}>
                            <h3 className='stickerFinalPrice'>{props.finalPrice} €</h3>
                        </Grid>
                        <Grid xs = {2} display="flex" justifyContent="center">
                            <IconButton onClick={(event) => props.onRemoveItemFromCart(props.cartItemId)}
                                        size='small' aria-label="delete"
                                        sx={{padding: "21px"}}
                                        >
                                <ion-icon name="close-circle-outline" style={{color: GLOBAL_CONSTANTS.COLORS.colorB}}></ion-icon>
                            </IconButton>

                        </Grid>
                    </Grid>                
                </div>
        ); 

    }else if (props.type == 'canvas'){
        return(  
            <div>
                <Grid   container 
                        justifyContent="center"
                        alignItems="center">
                    <Grid xs = {1.5} sm = {1}>
                        <h3 className='stickerListIndex'>{props.listIndex}</h3>
                    </Grid>

                        <Grid xs = {3} sm = {2} display="flex" justifyContent="center">
                            <Link style={{pointerEvents: "none"}} to=''>
                                <div className='cartStickerImgWrapper'>
                                    <img className='cartStickerImg' src={props.img}></img>
                                </div>
                            </Link>
                        </Grid>

                    <Grid xs = {4.5} sm = {6}>
                        <Link to='' style={{pointerEvents: "none", textDecoration: 'none', color: 'inherit'}}>
                            <h3 className='stickerName'>{props.name} ({props.material}, {props.style}) x{props.amount}</h3>
                            <p className='stickerInfo'>{props.price} €/item, {props.width} mm x {props.height} mm</p>
                        </Link>
                    </Grid>
                    <Grid xs = {1} sm = {1}>
                        <h3 className='stickerFinalPrice'>{props.finalPrice} €</h3>
                    </Grid>
                    <Grid xs = {2} display="flex" justifyContent="center">
                        <IconButton onClick={(event) => props.onRemoveItemFromCart(props.cartItemId)}
                                    size='small' aria-label="delete"
                                    sx={{padding: "21px"}}
                                    >
                            <ion-icon name="close-circle-outline" style={{color: GLOBAL_CONSTANTS.COLORS.colorB}}></ion-icon>
                        </IconButton>
                    </Grid>
                </Grid>                
            </div>
        ); 
    }

}


export default CartItem
